import React, { Component } from 'react';
import NavigationBar from "./NavigationBar.js"
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Button from "@material-ui/core/Button";
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import FolderIcon from '@material-ui/icons/Folder';
import DeleteIcon from '@material-ui/icons/Delete';


import "./subs.json";
import axios from "axios";
import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { withStyles } from "@material-ui/core";

const StyledText = withStyles({
  root: {
    '& label.Mui-focused': {
      color: 'gray',
    },
    '& .MuiInput-underline:after': {
      borderBottomColor: '#D9D9D9',
    },
    '& .MuiOutlinedInput-root': {
      '& fieldset': {
        borderColor: 'white',
      },
      '&:hover fieldset': {
        borderColor: 'white',
      },
      '&.Mui-focused fieldset': {
        borderColor: '#D9D9D9',
      },
    },
    boxShadow: '0 3px 3px 2px rgba(240, 105, 135, .3)'
  },
})(TextField);

const StyledBtn = withStyles({
  root: {
      '& label.Mui-focused': {
        color: 'gray',
      },
      '& .MuiInput-underline:after': {
        borderBottomColor: '#D9D9D9',
      },
      '& .MuiOutlinedInput-root': {
        '& fieldset': {
          borderColor: 'white',
        },
        '&:hover fieldset': {
          borderColor: 'white',
        },
        '&.Mui-focused fieldset': {
          borderColor: '#D9D9D9',
        },
      }, 
      alignContent: 'left',
      minheight: '20%',
      display: 'inline-block'
  },
  
})(Button);

class Pantry extends Component {

  constructor(props) {
    super(props);
    this.state = {
      input: "",
      ingredients: [],
      suggestions: [],
      rawOptions: [],
      options: [],
      showBtn: false
    };
  }

  async componentDidMount() {
    await axios.get("http://127.0.0.1:8000/api/subs/").then( 
      response => { 
        var result = response.data; 
        this.setState({ rawOptions: result })
        }, 
    (error) => { 
    {alert(error)}; 
    } 
    ); 
    }

suggestionSelected (valuearray) {
  if (valuearray !== null) {
    
    this.setState({
      ingredient: valuearray.map(i => {
        return i.subname
      }),
      showBtn: true

    });
    /* this.props.sendData({ name: "ingredient", value: value.subname });
     */
  };
  }

  render() {
    return (
      <div className="App">
      <NavigationBar/>

      {/*Ingredient input box */}
      
      <div className="PantrySearch">
     <Autocomplete
      multiple
      name = 'ingredient'
      onChange = {(event, valuearray) => this.suggestionSelected(valuearray)} 
      id="autocomplete-box"
      options={this.state.rawOptions}
      getOptionLabel={(option) => option.subname}
      
      disableClearable 
      style={{ width: 700,
                display: 'inline-block',
                position: 'relative',
                background: 'white',
                alignContent: 'left',
                marginTop: '2%',
                '&$cssFocused $notchedOutline': {
                  borderColor: '#448cff',
              },
              '&:hover $notchedOutline':{
                  borderColor:'#FFFFFF'
              }
               }}
      renderInput={(params) => <StyledText {...params} label="Ingredient" variant="outlined" />}
    />
    {/*Submit button*/}
    <StyledBtn disabled = {this.showBtn} type="Submit" onClick={this.handleClick} className="Submit">
            Add
    </StyledBtn>
    </div>

    
      
    {/*Pantry list*/}
      <div style={{maxWidth: '50%', marginLeft: 'auto', marginRight: 'auto', marginTop: '2%', 
                  backgroundColor: '#d6d6d6'}}>

      <List dense>
                <ListItem>
                  <ListItemAvatar>
                    <Avatar>
                      <FolderIcon />
                    </Avatar>
                  </ListItemAvatar>
                  <ListItemText
                    primary="Single-line item"
                    secondary='Secondary text' 
                  />
                  <ListItemSecondaryAction>
                    <IconButton edge="end" aria-label="delete">
                      <DeleteIcon />
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>,
              
      </List>
      
      </div>
      </div>
    );
    

  }
}

export default Pantry;
