import React, { Component } from "react";
import "./styles.css";
import unitData from "./units";
import Autocomplete from "./Autocomplete.js";
import Results from "./Results.js"
import Select from "@material-ui/core/Select";
import Button from "@material-ui/core/Button";
import FormErrors from "./FormErrors.js";

class App extends Component {
  constructor() {
    super();
    this.state = {
      ingredient: "",
      amount: "",
      unit: "",
      showResult: false,
      errors: {ingredient: "", amount:"", unit: ""},
      ingredientValid: false,
      amountValid: false,
      unitValid: false,
      submissionValid: false
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidUpdate(prevProps, prevState){
    if (this.state.ingredient !== prevState.ingredient){
      this.setState({
        showResult: false
      })
    }
  }

  handleChange(event) {
    const { name, value } = event.target ? event.target : event;
    this.setState(
      {[name]: value},
      () => {this.validation(name, value)})
    }

  validation(name, value) {
    let ingredientValid = this.state.ingredientValid
    let fieldErrors = this.state.errors
    fieldErrors[name]= value.length > 0 ? '': 'Please choose a valid' + name
    this.setState({
      errors: fieldErrors
    })
  }

  handleSubmit(event) {
    event.preventDefault()
    const errors = this.validate(this.state.ingredient, this.state.amount, this.state.unit)
    if (errors.length > 0) {
      this.setState({errors: errors})
    } else {
    this.setState({
      showResult: true
    })
  }}

  render() {
    const unitList = unitData.map(unit => (
      <option key={unit.index} value={unit.name}>
        {unit.name}
      </option>
    ));
    return (
      <div className="App">

        {/* Displays search errors at the top*/}
        <div className="errors">
          <FormErrors formErrors={this.state.errors}/>
        </div>

        <form onSubmit={this.handleSubmit}>
          {/* ingredient search */}
          <p>Search for your ingredient</p>
          <Autocomplete 
            sendData={this.handleChange}
          />

          {/* quantity textbox */}
          <p>Enter the quantity:</p>
          <input
            type="text"
            name="amount"
            value={this.state.amount}
            onChange={this.handleChange}
          />

          {/* units drop-down */}
          <Select
            name="unit"
            className="Select"
            value={this.state.unit}
            onChange={this.handleChange}
          >
            {unitList}
          </Select>

          {/*submit button */}
          <br />
          <Button disabled type="Submit" variant="outlined" onClick={this.handleClick} className="Submit">
            Find substitution!
          </Button>
        </form>

		<div className="Results">
		{this.state.showResult && <Results 
			ingredient={this.state.ingredient} 
			amount={this.state.amount} 
			unit={this.state.unit}
			showResult={this.state.showResult}
		/>}	
        </div>
      </div>
    );
  }
}
export default App;
