import React, { Component } from "react";
import PropTypes from "prop-types";
import "./styles.css";
import "./subs.json";
import axios from "axios";

class Autocomplete extends Component {
  constructor(props) {
    super(props);
    /* ToDO: replace with call to postgresql db to retrieve data in json format*/
    
    this.state = {
      input: "",
      suggestions: [],
      rawOptions: [],
      options: []
    };
  }

  async componentDidMount() {
    const subResponse = await axios.get("http://127.0.0.1:8000/api/subs/").then( 
      (response) => { 
        var result = response.data; 
        console.log('Processing Request'); 
        this.setState({ rawOptions: result })
        }, 
        (error) => { 
          {alert(error)}; 
        } 
    ); 
    let optionsArray = [];
    for (var i in this.rawOptions) {
      console.log(this.rawOptions[i])
      var item = this.rawOptions[i];
      optionsArray.push(item.subname);
    }
}

onTextChanged = event => {
    const value = event.target.value;
    this.setState({options: optionsArray})
    let suggestions = [];
    if (value.length > 0) {
      const regex = new RegExp(`^${value}`, "i");
      suggestions = this.options.sort().filter(value => regex.test(value));
    }
    this.setState({ suggestions: suggestions, input: value });
  };

suggestionSelected(value) {
    this.setState({
      input: value,
      suggestions: []
    });
    this.props.sendData({ name: "ingredient", value: value });
  }

renderSuggestions() {
    const { suggestions } = this.state;
    if (suggestions.length === 0) {
      return null;
    } else {
      return (
        <div>
          <ul>
            {suggestions.map(item => (
              <li onClick={() => this.suggestionSelected(item)}>{item}</li>
            ))}
          </ul>
        </div>
      );
    }
  }

render() {
    return (
      <div className="AutoComplete">
        <input
          value={this.state.input}
          type="text"
          onChange={this.onTextChanged}
        />
        {this.renderSuggestions()}
      </div>
    );
  }
}

export default Autocomplete;
