import React, {Component} from "react";
import axios from "axios";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import LinearProgress from '@material-ui/core/LinearProgress';
import { rgbToHex } from "@material-ui/core";
/* eslint-disable */


/* Displays the substitution results at the bottom of the main page */


class Results extends Component {
    constructor(props) {
		super(props)
		this.state = {
			result: [],
			subslist: [],
			userIngredients: JSON.parse(localStorage.getItem('userPantry'))
		}
	}

	
	async componentDidMount() {
		await axios.get(encodeURI("http://127.0.0.1:8000/api/subs/?format=json&subname="
		.concat((this.props.ingredient).toString())
			)).then( 
        response => { 
			var result = response.data; 
			this.setState({ result: result })
            }, 
            (error) => { 
            {alert(error)}; 
            } 
		)
		} 
	
	async componentDidUpdate() {
		if (this.state.result[0].subname !== this.props.ingredient ){
		await axios.get(encodeURI("http://127.0.0.1:8000/api/subs/?format=json&subname="
		.concat((this.props.ingredient).toString())
			)).then( 
        response => { 
			var result = response.data; 
			this.setState({ result: result })
            }, 
            (error) => { 
            {alert(error)}; 
            } 
		)
		} 
	}		

	render() {
		if (this.state.result.length > 0) {
			var substlist = this.state.result[0].subslist
			const ingredientList = Object.entries(substlist).map(t => t[0])
			
			var userHasIngrs = ingredientList.every(i => this.state.userIngredients.includes(i))
		}
			
		return(
			//this.state.result.length > 0
			 (false) ? (
				
				(userHasIngrs !== false) ? (
					<div className="Results">
					<div className="List">
						<List>
							{Object.entries(substlist).map((t, k) => (
							<ListItem className="ListItem">
								<ListItemText 
									primary={t[0]} 
									secondary={(t[1] * this.props.amount).toFixed(2) + " " + this.props.unit }/>
							</ListItem>))}
						</List>	 
					</div>
					</div>
				) : (<div style={{maxWidth: '775px', minWidth: '500px', width: '500px', marginTop: '10px', 
				backgroundColor: '#ffd1df', display: 'flex', justifyContent: 'center', alignItems: 'center', marginLeft: 'auto', marginRight: 'auto'}}>
						<h3 style={{color:'gray', fontSize:'25px', backgroundColor: 'rgb(255,255,255, .5)', padding: '7px', marginTop: 'auto', marginBottom: 'auto'}}>Your pantry does not have any of the substitute ingredients</h3>
					</div>)
			) : (<div style={{maxWidth: '150px', marginLeft: 'auto', marginRight:'auto', marginTop: '3%', height: '30px'}}>
					<LinearProgress color="secondary"/><p style={{backgroundColor: 'white', opacity: '0.7', paddingBottom: '15px' }}><br />Loading...</p>
				</div>)
        )
    }
}

export default Results
